import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudyPage.css';

export default function StudyPage() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const fileInputRef = useRef(null);

  // Background style
  const bgStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages4.jpg)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  };

  // Summarise state
  const [rawNotes, setRawNotes] = useState('');
  const [file, setFile] = useState(null);
  const [summary, setSummary] = useState('');
  const [loadingSum, setLoadingSum] = useState(false);

  // Paraphrase state
  const [text, setText] = useState('');
  const [tone, setTone] = useState('casual');
  const [paraphrase, setParaphrase] = useState('');
  const [loadingPara, setLoadingPara] = useState(false);

  // Vocab search state
  const [searchWord, setSearchWord] = useState('');
  const [vocabDef, setVocabDef] = useState('');
  const [loadingGenWord, setLoadingGenWord] = useState(false);
  const [loadingSearch, setLoadingSearch] = useState(false);

  // Manual vocab state
  const [manualWord, setManualWord] = useState('');
  const [manualMeaning, setManualMeaning] = useState('');

  // Manual extraction state
  const [manualText, setManualText] = useState('');
  const [extractedManual, setExtractedManual] = useState([]);
  const [loadingExtractManual, setLoadingExtractManual] = useState(false);

  // AI extraction state
  const [aiParagraph, setAiParagraph] = useState('');
  const [extractedAi, setExtractedAi] = useState([]);
  const [loadingGenAiPara, setLoadingGenAiPara] = useState(false);
  const [loadingExtractAi, setLoadingExtractAi] = useState(false);

  // UI message
  const [message, setMessage] = useState('');

  // Summarize handlers
  const genNotes = async () => {
    setLoadingSum(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-note', { method: 'POST' });
      const { note } = await res.json();
      setRawNotes(note || '');
    } catch {
      setMessage('Error generating notes');
    } finally {
      setLoadingSum(false);
    }
  };

  const doSumm = async () => {
    if (!rawNotes.trim() && !file) {
      setMessage('Paste notes or choose a file');
      return;
    }
    setLoadingSum(true);
    setMessage('');
    try {
      let data;
      if (rawNotes.trim()) {
        data = await (await fetch('http://127.0.0.1:5001/summarize-text', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: rawNotes }),
        })).json();
      } else {
        const form = new FormData();
        form.append('file', file);
        data = await (await fetch('http://127.0.0.1:5001/summarize', { method: 'POST', body: form })).json();
      }
      setSummary(data.summary || '');
    } catch {
      setMessage('Error summarising');
    } finally {
      setLoadingSum(false);
    }
  };

  const saveSum = async () => {
    if (!summary) return;
    await fetch('http://127.0.0.1:5001/save-summary', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, content: summary }),
    });
    setMessage('Summary saved');
  };

  // Paraphrase handlers
  const doPara = async () => {
    if (!text.trim()) {
      setMessage('Enter text');
      return;
    }
    setLoadingPara(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/paraphrase-text', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text, tone }),
      });
      const { content } = await res.json();
      setParaphrase(content || '');
    } catch {
      setMessage('Error paraphrasing');
    } finally {
      setLoadingPara(false);
    }
  };

  const savePara = async () => {
    if (!paraphrase) return;
    await fetch('http://127.0.0.1:5001/save-paraphrase', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, content: paraphrase, tone }),
    });
    setMessage('Paraphrase saved');
  };

  // Vocab search handlers
  const genWord = async () => {
    setLoadingGenWord(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-word', { method: 'POST' });
      const { word } = await res.json();
      setSearchWord(word || '');
    } catch {
      setMessage('Error generating word');
    } finally {
      setLoadingGenWord(false);
    }
  };

  const defineWord = async () => {
    if (!searchWord.trim()) {
      setMessage('Enter a word');
      return;
    }
    setLoadingSearch(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/define-word', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ word: searchWord.trim() }),
      });
      const { content } = await res.json();
      setVocabDef(content || '');
    } catch {
      setMessage('Error defining word');
    } finally {
      setLoadingSearch(false);
    }
  };

  const saveVocab = async () => {
    if (!searchWord || !vocabDef) return;
    await fetch('http://127.0.0.1:5001/save-vocab', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, word: searchWord, definition: vocabDef }),
    });
    setMessage('Vocab saved');
  };

  // Manual vocab handlers
  const saveManual = async () => {
    if (!manualWord.trim() || !manualMeaning.trim()) {
      setMessage('Fill both fields');
      return;
    }
    await fetch('http://127.0.0.1:5001/save-vocab', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, word: manualWord, definition: manualMeaning }),
    });
    setMessage('Manual vocab saved');
  };

  // Manual extraction handlers
  const extractManual = async () => {
    if (!manualText.trim()) {
      setMessage('Paste a paragraph');
      return;
    }
    setLoadingExtractManual(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/extract-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: manualText }),
      });
      const { words } = await res.json();
      setExtractedManual(words || []);
    } catch {
      setMessage('Extraction failed');
    } finally {
      setLoadingExtractManual(false);
    }
  };

  const clearManual = () => {
    setManualText('');
    setExtractedManual([]);
    setMessage('');
  };

  const saveExtractedManual = async () => {
    for (const w of extractedManual) {
      await fetch('http://127.0.0.1:5001/save-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, word: w, definition: '' }),
      });
    }
    setMessage('Extracted vocab saved');
  };

  // AI extraction handlers
  const generateAiPara = async () => {
    setLoadingGenAiPara(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-note', { method: 'POST' });
      const { note } = await res.json();
      setAiParagraph(note || '');
    } catch {
      setMessage('Error generating paragraph');
    } finally {
      setLoadingGenAiPara(false);
    }
  };

  const extractAi = async () => {
    if (!aiParagraph.trim()) {
      setMessage('Generate paragraph first');
      return;
    }
    setLoadingExtractAi(true);
    setMessage('');
    try {
      const res = await fetch('http://127.0.0.1:5001/extract-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: aiParagraph }),
      });
      const { words } = await res.json();
      setExtractedAi(words || []);
    } catch {
      setMessage('Extraction failed');
    } finally {
      setLoadingExtractAi(false);
    }
  };

  const clearAi = () => {
    setAiParagraph('');
    setExtractedAi([]);
    setMessage('');
  };

  const saveExtractedAi = async () => {
    for (const w of extractedAi) {
      await fetch('http://127.0.0.1:5001/save-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, word: w, definition: '' }),
      });
    }
    setMessage('AI extracted vocab saved');
  };

  return (
    <div className="study-page" style={bgStyle}>
      <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" className="home-icon" onClick={() => navigate('/home2')} />
      <h1 className="main-header">Let’s Study</h1>
      {message && <div className="message">{message}</div>}

      {/* Summarise Note */}
      <section className="panel">
        <h2>Summarise Note</h2>
        <textarea rows="4" placeholder="Paste notes…" value={rawNotes} onChange={e => setRawNotes(e.target.value)} />
        <div className="controls">
          <button onClick={() => setRawNotes('')}>Clear Text</button>
          <input ref={fileInputRef} type="file" accept=".txt,.pdf" onChange={e => setFile(e.target.files[0])} />
          <button onClick={() => { setFile(null); fileInputRef.current.value = ''; }}>Clear File</button>
          <button onClick={genNotes} disabled={loadingSum}>{loadingSum ? 'Generating…' : 'Generate'}</button>
        </div>
        <button onClick={doSumm} disabled={loadingSum}>{loadingSum ? 'Summarising…' : 'Summarise'}</button>
        {summary && (
          <div className="output">
            <div dangerouslySetInnerHTML={{ __html: summary }} />
            <div className="actions">
              <button onClick={saveSum}>Save</button>
              <button onClick={() => { navigator.clipboard.writeText(summary); setMessage('Copied'); }}>Copy</button>
              <button onClick={() => setSummary('')}>Clear</button>
            </div>
          </div>
        )}
      </section>

      {/* Paraphrase Note */}
      <section className="panel">
        <h2>Paraphrase Note</h2>
        <div className="controls">
          <button onClick={doPara} disabled={loadingPara}>{loadingPara ? 'Paraphrasing…' : 'Paraphrase'}</button>
          <button onClick={() => setText('')}>Clear Text</button>
        </div>
        <textarea rows="4" placeholder="Enter text…" value={text} onChange={e => setText(e.target.value)} />
        <div className="controls">
          <select value={tone} onChange={e => setTone(e.target.value)}>
            <option value="casual">Casual</option>
            <option value="academic">Academic</option>
          </select>
        </div>
        {paraphrase && (
          <div className="output">
            <div dangerouslySetInnerHTML={{ __html: paraphrase }} />
            <div className="actions">
              <button onClick={savePara}>Save</button>
              <button onClick={() => { navigator.clipboard.writeText(paraphrase); setMessage('Copied'); }}>Copy</button>
              <button onClick={() => setParaphrase('')}>Clear</button>
            </div>
          </div>
        )}
      </section>

      {/* Vocab Search & Manual Insert */}
      <div className="row">
        <section className="panel">
          <h2>Vocab Search</h2>
          <div className="controls">
            <button onClick={genWord} disabled={loadingGenWord}>{loadingGenWord ? 'Generating…' : 'Generate'}</button>
            <button onClick={() => { setSearchWord(''); setVocabDef(''); }}>Clear</button>
          </div>
          <input type="text" placeholder="Enter word…" value={searchWord} onChange={e => setSearchWord(e.target.value)} />
          <button onClick={defineWord} disabled={loadingSearch}>{loadingSearch ? 'Searching…' : 'Search'}</button>
          {vocabDef && <div className="output">{vocabDef}</div>}
          {vocabDef && <button onClick={saveVocab}>Save</button>}
        </section>
        <section className="panel">
          <h2>Manual Vocab</h2>
          <input type="text" placeholder="Word…" value={manualWord} onChange={e => setManualWord(e.target.value)} />
          <textarea rows="3" placeholder="Meaning…" value={manualMeaning} onChange={e => setManualMeaning(e.target.value)} />
          <div className="controls">
            <button onClick={saveManual}>Save</button>
            <button onClick={() => { setManualWord(''); setManualMeaning(''); }}>Clear</button>
          </div>
        </section>
      </div>

      {/* Extraction Panels */}
      <div className="row">
        <section className="panel">
          <h2>Extract Vocab (Manual)</h2>
          <textarea rows="4" placeholder="Paste a paragraph…" value={manualText} onChange={e => setManualText(e.target.value)} />
          <div className="controls">
            <button onClick={extractManual} disabled={loadingExtractManual}>{loadingExtractManual ? 'Extracting…' : 'Extract'}</button>
            <button onClick={clearManual}>Clear</button>
          </div>
          {extractedManual.length > 0 && (
            <>
              <ul className="list">{extractedManual.map(w => <li key={w}>{w}</li>)}</ul>
              <button onClick={saveExtractedManual}>Save All</button>
            </>
          )}
        </section>
        <section className="panel">
          <h2>Extract Vocab (AI)</h2>
          <div className="controls">
            <button onClick={generateAiPara} disabled={loadingGenAiPara}>{loadingGenAiPara ? 'Generating…' : 'Generate Paragraph'}</button>
            <button onClick={clearAi}>Clear</button>
          </div>
          {aiParagraph && <div className="output">{aiParagraph}</div>}
          {aiParagraph && <button onClick={extractAi} disabled={loadingExtractAi}>{loadingExtractAi ? 'Extracting…' : 'Extract'}</button>}
          {extractedAi.length > 0 && (
            <>
              <ul className="list">{extractedAi.map(w => <li key={w}>{w}</li>)}</ul>
              <button onClick={saveExtractedAi}>Save All</button>
            </>
          )}
        </section>
      </div>
    </div>
  );
}
