// src/pages/StudyPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudyPage.css';

export default function StudyPage() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const fileInputRef = useRef(null);

  // Background
  const bgStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages4.jpg)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  };

  // STATE
  const [rawNotes, setRawNotes] = useState('');
  const [file, setFile] = useState(null);
  const [summary, setSummary] = useState('');
  const [loadingSum, setLoadingSum] = useState(false);

  const [text, setText] = useState('');
  const [tone, setTone] = useState('casual');
  const [paraphrase, setParaphrase] = useState('');
  const [loadingPara, setLoadingPara] = useState(false);

  const [searchWord, setSearchWord] = useState('');
  const [vocabDef, setVocabDef] = useState('');
  const [loadingGenWord, setLoadingGenWord] = useState(false);
  const [loadingSearch, setLoadingSearch] = useState(false);

  const [manualWord, setManualWord] = useState('');
  const [manualMeaning, setManualMeaning] = useState('');

  const [manualText, setManualText] = useState('');
  const [extractedManual, setExtractedManual] = useState([]);
  const [loadingExtractManual, setLoadingExtractManual] = useState(false);

  const [aiParagraph, setAiParagraph] = useState('');
  const [extractedAi, setExtractedAi] = useState([]);
  const [loadingGenAiPara, setLoadingGenAiPara] = useState(false);
  const [loadingExtractAi, setLoadingExtractAi] = useState(false);

  const [popupMessage, setPopupMessage] = useState('');

  // Load user-saved data (not shown)
  useEffect(() => {
    // optionally load history
  }, []);

  // POPUP HANDLING
  const showPopup = (msg) => {
    setPopupMessage(msg);
    setTimeout(() => setPopupMessage(''), 2000);
  };

  // Handlers
  const genNotes = async () => { setLoadingSum(true); try { const res = await fetch('http://127.0.0.1:5001/generate-note',{method:'POST'}); const {note} = await res.json(); setRawNotes(note||''); } catch { } finally { setLoadingSum(false);} };
  const doSumm = async () => {
    if (!rawNotes.trim() && !file) return;
    setLoadingSum(true);
    try {
      let data;
      if (rawNotes.trim()) {
        const res = await fetch('http://127.0.0.1:5001/summarize-text', {method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text:rawNotes})});
        data = await res.json();
      } else {
        const f = new FormData(); f.append('file', file);
        const res = await fetch('http://127.0.0.1:5001/summarize',{method:'POST',body:f});
        data = await res.json();
      }
      setSummary(data.summary||'');
    } catch {} finally { setLoadingSum(false); }
  };
  const saveSum = async () => {
    if (!summary) return;
    try {
      await fetch('http://127.0.0.1:5001/save-summary',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,content:summary})});
      showPopup('Saved summary!');
    } catch {};
  };

  const genExample = async () => { setLoadingPara(true); try { const res = await fetch('http://127.0.0.1:5001/generate-note',{method:'POST'}); const {note} = await res.json(); setText(note||''); } catch {} finally { setLoadingPara(false);} };
  const doPara = async () => { if (!text.trim()) return; setLoadingPara(true); try { const res = await fetch('http://127.0.0.1:5001/paraphrase-text',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text,tone})}); const {content} = await res.json(); setParaphrase(content||''); } catch {} finally { setLoadingPara(false);} };
  const savePara = async () => { if (!paraphrase) return; try { await fetch('http://127.0.0.1:5001/save-paraphrase',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,content:paraphrase,tone})}); showPopup('Saved paraphrase!'); } catch {} };

  const genWord = async () => { setLoadingGenWord(true); try { const res = await fetch('http://127.0.0.1:5001/generate-word',{method:'POST'}); const {word} = await res.json(); setSearchWord(word||''); } catch {} finally { setLoadingGenWord(false);} };
  const defineWord = async () => { if (!searchWord.trim()) return; setLoadingSearch(true); try { const res = await fetch('http://127.0.0.1:5001/define-word',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({word:searchWord})}); const {content} = await res.json(); setVocabDef(content||''); } catch {} finally { setLoadingSearch(false);} };
  const saveVocab = async () => { if (!vocabDef) return; try { await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:searchWord,definition:vocabDef})}); showPopup('Saved vocab!'); } catch {} };

  const extractManual = async () => { if (!manualText.trim()) return; setLoadingExtractManual(true); try { const res = await fetch('http://127.0.0.1:5001/extract-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text:manualText})}); const {words} = await res.json(); setExtractedManual(words||[]); } catch {} finally { setLoadingExtractManual(false);} };
  const saveManualExtract = async () => { if (!extractedManual.length) return; try { for (let w of extractedManual) await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:w,definition:''})}); showPopup('Saved extracted!'); } catch {} };

  const genAi = async () => { setLoadingGenAiPara(true); try { const res = await fetch('http://127.0.0.1:5001/generate-note',{method:'POST'}); const {note} = await res.json(); setAiParagraph(note||''); } catch {} finally { setLoadingGenAiPara(false);} };
  const extractAi = async () => { if (!aiParagraph) return; setLoadingExtractAi(true); try { const res = await fetch('http://127.0.0.1:5001/extract-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text:aiParagraph})}); const {words} = await res.json(); setExtractedAi(words||[]); } catch {} finally { setLoadingExtractAi(false);} };
  const saveAiExtract = async () => { if (!extractedAi.length) return; try { for (let w of extractedAi) await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:w,definition:''})}); showPopup('Saved AI extracted!'); } catch {} };

  return (
    <div className="study-page" style={bgStyle}>
      {popupMessage && <div className="popup-glass"><p>{popupMessage}</p></div>}
      <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" className="streak-home" onClick={()=>navigate('/home2')} />
      <h1 className="main-header">Let’s Study</h1>

      <div className="top-row">
        <section className="section">
          <h2>Summarise Note</h2>
          <input type="file" ref={fileInputRef} accept=".txt,.pdf" onChange={e=>setFile(e.target.files[0])} />
          <textarea rows="4" value={rawNotes} onChange={e=>setRawNotes(e.target.value)} placeholder="Paste notes…" />
          <div className="action-buttons">
            <button onClick={doSumm} disabled={loadingSum}>{loadingSum?'Summarising…':'Summarise'}</button>
            <button onClick={genNotes} disabled={loadingSum}>{loadingSum?'Generating…':'Generate'}</button>
            <button onClick={()=>{setRawNotes('');setFile(null);fileInputRef.current.value='';}}>Clear</button>
          </div>
          {summary && <div className="generated-output"><div dangerouslySetInnerHTML={{__html:summary}}/><button className="btn" onClick={saveSum}>Save</button></div>}
        </section>

        <section className="section">
          <h2>Paraphrase Note</h2>
          <textarea rows="4" value={text} onChange={e=>setText(e.target.value)} placeholder="Enter text…" />
          <div className="action-buttons">
            <button onClick={doPara} disabled={loadingPara}>{loadingPara?'Paraphrasing…':'Paraphrase'}</button>
            <button onClick={genExample} disabled={loadingPara}>{loadingPara?'Generating…':'Generate'}</button>
            <button onClick={()=>setText('')}>Clear</button>
          </div>
          <select value={tone} onChange={e=>setTone(e.target.value)}><option value="casual">Casual</option><option value="academic">Academic</option></select>
          {paraphrase && <div className="generated-output"><div dangerouslySetInnerHTML={{__html:paraphrase}}/><button className="btn" onClick={savePara}>Save</button></div>}
        </section>
      </div>

      <div className="middle-row">
        <section className="section">
          <h2>Vocab Search</h2>
          <input type="text" value={searchWord} onChange={e=>setSearchWord(e.target.value)} placeholder="Enter word…" />
          <div className="action-buttons">
            <button onClick={defineWord} disabled={loadingSearch}>{loadingSearch?'Searching…':'Search'}</button>
            <button onClick={genWord} disabled={loadingGenWord}>{loadingGenWord?'Generating…':'Generate'}</button>
            <button onClick={()=>{setSearchWord('');setVocabDef('');}}>Clear</button>
          </div>
          {vocabDef && <div className="generated-output"><p>{vocabDef}</p><button className="btn" onClick={saveVocab}>Save</button></div>}
        </section>

        <section className="section">
          <h2>Manual Vocab</h2>
          <input type="text" value={manualWord} onChange={e=>setManualWord(e.target.value)} placeholder="Word…" />
          <textarea rows="3" value={manualMeaning} onChange={e=>setManualMeaning(e.target.value)} placeholder="Meaning…" />
          <button className="btn" onClick={saveManual}>Save</button>
        </section>
      </div>

      <div className="extract-row">
        <section className="section">
          <h2>Extract Vocab (Manual)</h2>
          <textarea rows="4" value={manualText} onChange={e=>setManualText(e.target.value)} placeholder="Paste paragraph…" />
          <div className="action-buttons">
            <button onClick={extractManual} disabled={loadingExtractManual}>{loadingExtractManual?'Extracting…':'Extract'}</button>
            <button onClick={()=>{setManualText('');setExtractedManual([]);}}>Clear</button>
          </div>
          {extractedManual.length>0 && <div className="generated-output"><ul className="extracted-list">{extractedManual.map(w=><li key={w}>{w}</li>)}</ul><button className="btn" onClick={saveManualExtract}>Save</button></div>}
        </section>

        <section className="section">
          <h2>Extract Vocab (AI)</h2>
          <div className="action-buttons">
            <button onClick={extractAi} disabled={loadingExtractAi}>{loadingExtractAi?'Extracting…':'Extract'}</button>
            <button onClick={genAi} disabled={loadingGenAiPara}>{loadingGenAiPara?'Generating…':'Generate Paragraph'}</button>
            <button onClick={()=>{setAiParagraph('');setExtractedAi([]);}}>Clear</button>
          </div>
          {aiParagraph && <div className="generated-output"><p>{aiParagraph}</p></div>}
          {extractedAi.length>0 && <div className="generated-output"><ul className="extracted-list">{extractedAi.map(w=><li key={w}>{w}</li>)}</ul><button className="btn" onClick={saveAiExtract}>Save</button></div>}        
        </section>
      </div>
    </div>
  );
}
