import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './SavedPage.css';

const endpoints = {
  Summaries: 'summaries',
  Paraphrases: 'paraphrases',
  Vocab: 'vocab-list',
};

export default function SavePage() {
  const tabs = ['Summaries', 'Paraphrases', 'Vocab'];
  const [activeTab, setActiveTab] = useState(tabs[0]);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const username = localStorage.getItem('username');

  useEffect(() => {
    const fetchItems = async () => {
      setLoading(true);
      setMessage('');
      try {
        const res = await fetch(
          `http://127.0.0.1:5001/${endpoints[activeTab]}?username=${username}`
        );
        if (!res.ok) throw new Error(res.statusText);
        const data = await res.json();
        setItems(data);
      } catch (err) {
        console.error(err);
        setMessage(`Failed to load ${activeTab.toLowerCase()}.`);
      } finally {
        setLoading(false);
      }
    };
    if (username) fetchItems();
  }, [activeTab, username]);

  const bgStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages4.jpg)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  };

  return (
    <div className="savepage-container" style={bgStyle}>
      <Link to="/study" className="back-home">← Back to Study</Link>
      <h1>Saved Items</h1>

      {/* Tab bar */}
      <div className="tab-bar">
        {tabs.map(tab => (
          <button
            key={tab}
            className={`tab-btn ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Content */}
      {loading ? (
        <p className="status-text">Loading {activeTab.toLowerCase()}…</p>
      ) : message ? (
        <p className="status-text">{message}</p>
      ) : items.length === 0 ? (
        <p className="status-text">No {activeTab.toLowerCase()} saved yet.</p>
      ) : (
        <ul className="item-list">
          {activeTab === 'Summaries' &&
            items.map(i => (
              <li key={i._id} className="item">
                <div
                  className="item-content"
                  dangerouslySetInnerHTML={{ __html: i.content }}
                />
                <span className="item-date">
                  {new Date(i.createdAt).toLocaleString()}
                </span>
              </li>
            ))}

          {activeTab === 'Paraphrases' &&
            items.map(i => (
              <li key={i._id} className="item">
                <div
                  className="item-content"
                  dangerouslySetInnerHTML={{ __html: i.content }}
                />
                <span className="item-date">
                  {new Date(i.createdAt).toLocaleString()}
                </span>
              </li>
            ))}

          {activeTab === 'Vocab' &&
            items.map(v => (
              <li key={v._id} className="item">
                <strong>{v.word}</strong>: {v.definition}
              </li>
            ))}
        </ul>
      )}
    </div>
  );
}
