// client/src/pages/PlayPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

// NOTE: weâ€™re not importing PlayPage.css so all header styles are inline now

const API = 'https://study-classmate-server.onrender.com';

export default function PlayPage() {
  const navigate = useNavigate();
  const TODAY = new Date().toDateString();
  const username = localStorage.getItem('username');

  // GENERAL
  const [activeTab, setActiveTab] = useState('Play');
  const [diamonds, setDiamonds] = useState(20);
  const [scores, setScores] = useState([]);

  // CARD SELECTION
  const [mode, setMode] = useState('select');
  const [cards, setCards] = useState([]);
  const [count, setCount] = useState(5);
  const [manualList, setManualList] = useState(
    Array.from({ length: 5 }, () => ({ word: '', definition: '' }))
  );
  const [loadingGen, setLoadingGen] = useState(false);

  // QUIZ
  const [quizCards, setQuizCards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [timer, setTimer] = useState(15);
  const timerRef = useRef(null);
  const [guess, setGuess] = useState('');
  const [results, setResults] = useState([]);

  // MODALS
  const [showSkipConfirm, setShowSkipConfirm] = useState(false);
  const [showName, setShowName] = useState(false);
  const [playerName, setPlayerName] = useState('');
  const [sortBy, setSortBy] = useState('highest');

  // Load initial game state
  useEffect(() => {
    fetch(`${API}/game-state?username=${username}`)
      .then(r => r.json())
      .then(data => {
        setDiamonds(data.diamonds);
        setScores(data.scores);
      })
      .catch(console.error);
  }, [username]);

  // Timer logic
  const startTimer = () => {
    clearInterval(timerRef.current);
    setTimer(15);
    timerRef.current = setInterval(() => {
      setTimer(t => {
        if (t <= 1) {
          clearInterval(timerRef.current);
          promptSkip(); // autoâ€skip
          return 15;
        }
        return t - 1;
      });
    }, 1000);
  };

  // Answer or skip
  const handleAnswer = async (submitted, isSkip = false) => {
    clearInterval(timerRef.current);

    if (isSkip && diamonds < 10) {
      return setShowSkipConfirm(true);
    }
    if (isSkip) {
      const newD = diamonds - 10;
      setDiamonds(newD);
      await fetch(`${API}/update-diamonds`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, diamonds: newD }),
      }).catch(console.error);
    }

    let correct = false;
    if (!isSkip && submitted.trim()) {
      correct =
        submitted.trim().toLowerCase() ===
        quizCards[currentIndex].word.toLowerCase();
    }

    const updated = [...results, { ...quizCards[currentIndex], correct }];
    setResults(updated);

    if (updated.length >= quizCards.length) {
      setShowName(true);
    } else {
      setCurrentIndex(i => i + 1);
      setGuess('');
      startTimer();
    }
  };

  // Skip helpers
  const promptSkip = () => setShowSkipConfirm(true);
  const confirmSkip = () => {
    setShowSkipConfirm(false);
    handleAnswer('', true);
  };

  // Generate via AI
  const handleGenerate = async () => {
    setLoadingGen(true);
    try {
      const setWords = new Set();
      while (setWords.size < count) {
        const r = await fetch(`${API}/generate-word`, { method: 'POST' });
        const { word } = await r.json();
        setWords.add(word);
      }
      const words = Array.from(setWords);
      const defs = await Promise.all(
        words.map(async w => {
          const res = await fetch(`${API}/define-word`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ word: w }),
          });
          const { content } = await res.json();
          const head = content.split(/Example sentence/i)[0];
          const m = head.match(/Definition:\s*([^-\n]+)/i);
          return { word: w, definition: m ? m[1].trim() : head.trim() };
        })
      );
      setCards(defs);
      setMode('review');
    } catch (e) {
      console.error(e);
    } finally {
      setLoadingGen(false);
    }
  };

  // Manual review
  const handleReviewManual = () => {
    setCards(manualList.slice(0, count));
    setMode('review');
  };

  // Start quiz
  const handleStartQuiz = () => {
    setQuizCards([...cards].sort(() => Math.random() - 0.5));
    setResults([]);
    setCurrentIndex(0);
    startTimer();
    setMode('quiz');
  };

  // Save score
  const saveScore = async () => {
    if (!playerName.trim()) return;
    const correctCount = results.filter(r => r.correct).length;
    const res = await fetch(`${API}/save-score`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        username,
        name: playerName,
        score: correctCount,
      }),
    });
    const { scores: updated } = await res.json();
    setScores(updated);
    setShowName(false);
    setMode('select');
    setPlayerName('');

    if (localStorage.getItem('lastQuizDate') !== TODAY) {
      localStorage.setItem('lastQuizDate', TODAY);
      // bump streak
      const r1 = await fetch(`${API}/get-streak?username=${username}`);
      const { streakDays } = await r1.json();
      await fetch(`${API}/save-streak`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, streakDays: (streakDays || 0) + 1 }),
      });
    }
  };

  // Sort scores
  const sorted = [...scores].sort((a, b) => {
    if (sortBy === 'highest') return b.score - a.score;
    if (sortBy === 'lowest') return a.score - b.score;
    if (sortBy === 'earliest') return new Date(a.time) - new Date(b.time);
    return new Date(b.time) - new Date(a.time);
  });

    return (
        <div
          style={{
            position: 'relative',
            fontFamily: "'Luckiest Guy', cursive",
            color: '#fff',
            minHeight: '100vh',
            backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages3.jpg)`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          {/* HEADER */}
          <div
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              height: '3rem',
              padding: '0 1rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              background: 'rgba(0, 0, 0, 0.6)',
              zIndex: 999,
            }}
          >
            <div
              style={{ cursor: 'pointer' }}
              onClick={() => navigate('/home2')}
            >
              <img
                src={`${process.env.PUBLIC_URL}/photos/home.jpg`}
                alt="Home"
                style={{ width: 32, height: 32 }}
              />
            </div>
            <h1 style={{ margin: 0, fontSize: '1.6rem', color: '#4caf50' }}>
              Letâ€™s Play
            </h1>
            <div style={{ display: 'flex', alignItems: 'center', gap: '.5rem' }}>
              <span style={{ fontSize: '1rem' }}>ðŸ’Ž {diamonds}</span>
              <button
                style={{
                  padding: '.3rem .8rem',
                  border: 'none',
                  borderRadius: 6,
                  background: '#4caf50',
                  color: '#fff',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                }}
                onClick={() =>
                  setActiveTab(t => (t === 'Play' ? 'Leaderboard' : 'Play'))
                }
              >
                {activeTab === 'Play' ? 'Leaderboard' : 'Back'}
              </button>
            </div>
          </div>

          {/* PLAY AREA, LEADERBOARD, MODALS */}
          {/* ... just paste the rest of your existing JSX here unchanged ... */}

          {/* SKIP CONFIRMATION */}
          {showSkipConfirm && (
            <div
              style={{
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: 'rgba(0,0,0,0.6)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 1000,
              }}
            >
              <div
                style={{
                  background: 'rgba(255,255,255,0.2)',
                  backdropFilter: 'blur(8px)',
                  border: '1px solid rgba(255,255,255,0.4)',
                  color: '#fff',
                  padding: '1rem 2rem',
                  borderRadius: 8,
                  textAlign: 'center',
                }}
              >
                <p>To skip, use 10 diamonds.</p>
                <div style={{ display: 'flex', gap: '.5rem', marginTop: '1rem' }}>
                  <button
                    style={{
                      flex: 1,
                      padding: '.5rem',
                      border: 'none',
                      borderRadius: 6,
                      background: 'rgba(255,255,255,0.3)',
                      color: '#000',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                    }}
                    onClick={confirmSkip}
                  >
                    Use
                  </button>
                  <button
                    style={{
                      flex: 1,
                      padding: '.5rem',
                      border: 'none',
                      borderRadius: 6,
                      background: 'rgba(255,255,255,0.3)',
                      color: '#000',
                      fontWeight: 'bold',
                      cursor: 'pointer',
                    }}
                    onClick={() => setShowSkipConfirm(false)}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* GAME OVER NAME PROMPT */}
          {showName && (
            <div
              style={{
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: 'rgba(0,0,0,0.6)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                zIndex: 1000,
              }}
            >
              <div
                style={{
                  background: '#fff',
                  padding: '1.5rem 2rem',
                  borderRadius: 8,
                  textAlign: 'center',
                  maxWidth: '90%',
                }}
              >
                <h3>Game Over!</h3>
                <p>Score: {results.filter(r => r.correct).length}</p>
                <input
                  placeholder="Enter your name"
                  value={playerName}
                  onChange={e => setPlayerName(e.target.value)}
                  style={{ width: '100%', boxSizing: 'border-box', margin: '1rem 0' }}
                />
                <button
                  style={{
                    padding: '.5rem 1rem',
                    border: 'none',
                    borderRadius: 6,
                    background: '#4caf50',
                    color: '#fff',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                  }}
                  onClick={saveScore}
                >
                  Save
                </button>
              </div>
            </div>
          )}
        </div>
      );
    }
