import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './CommonPage.css';

export default function MediaPage() {
  const [postText, setPostText] = useState('');
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const username = localStorage.getItem('username');

  const bgUrl = `${process.env.PUBLIC_URL}/photos/pages2.jpg`;

  // Fetch all user posts
  const fetchPosts = () => {
    setLoading(true);
    fetch('http://127.0.0.1:5001/all-media')
      .then(res => res.json())
      .then(data => setPosts(data.reverse()))
      .catch(err => {
        console.error('Failed to fetch posts:', err);
        setMessage('❌ Failed to load posts');
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  // Post new text
  const handlePost = async () => {
    if (!postText.trim()) return;
    try {
      const res = await fetch('http://127.0.0.1:5001/post-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, text: postText })
      });
      const data = await res.json();
      if (res.ok) {
        setPostText('');
        setMessage('✅ Post shared!');
        fetchPosts();
      } else {
        setMessage(data.message || '❌ Failed to post');
      }
    } catch (err) {
      console.error(err);
      setMessage('❌ Error posting');
    }
  };

  // Save post
  const savePost = async (postId) => {
    try {
      const res = await fetch('http://127.0.0.1:5001/save-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, postId })
      });
      if (res.ok) {
        setMessage('✅ Post saved!');
      } else {
        setMessage('❌ Failed to save');
      }
    } catch (err) {
      console.error(err);
      setMessage('❌ Error saving');
    }
  };

  return (
    <div
      className="common-page"
      style={{
        backgroundImage: `url(${bgUrl})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat'
      }}
    >
      <Link to="/home2" className="home-link">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>
      <h1>Media</h1>

      {message && (
        <div className="modal show">
          <div className="modal-content">
            <span>{message}</span>
          </div>
        </div>
      )}

      <div className="post-form">
        <textarea
          value={postText}
          onChange={e => setPostText(e.target.value)}
          placeholder="What's on your mind?"
        />
        <button onClick={handlePost}>Post</button>
      </div>

      <div className="post-feed">
        {loading ? (
          <p className="status-text">Loading posts…</p>
        ) : posts.length === 0 ? (
          <p className="status-text">No posts yet.</p>
        ) : (
          posts.map(p => (
            <div key={p._id} className="post-item">
              <p className="post-user">👤 {p.username}</p>
              <p className="post-text">{p.text}</p>
              <p className="post-date">{new Date(p.createdAt).toLocaleString()}</p>
              <button onClick={() => savePost(p._id)}>💾 Save</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
