// src/pages/StudyPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudyPage.css';

export default function StudyPage() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const fileInputRef = useRef(null);

  const bgStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages4.jpg)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  };

  const [rawNotes, setRawNotes] = useState('');
  const [file, setFile] = useState(null);
  const [summary, setSummary] = useState('');
  const [loadingSum, setLoadingSum] = useState(false);

  const [text, setText] = useState('');
  const [tone, setTone] = useState('casual');
  const [paraphrase, setParaphrase] = useState('');
  const [loadingPara, setLoadingPara] = useState(false);

  const [searchWord, setSearchWord] = useState('');
  const [vocabDef, setVocabDef] = useState('');
  const [loadingGenWord, setLoadingGenWord] = useState(false);
  const [loadingSearch, setLoadingSearch] = useState(false);

  const [manualWord, setManualWord] = useState('');
  const [manualMeaning, setManualMeaning] = useState('');

  const [manualText, setManualText] = useState('');
  const [extractedManual, setExtractedManual] = useState([]);
  const [loadingExtractManual, setLoadingExtractManual] = useState(false);

  const [aiParagraph, setAiParagraph] = useState('');
  const [extractedAi, setExtractedAi] = useState([]);
  const [loadingGenAiPara, setLoadingGenAiPara] = useState(false);
  const [loadingExtractAi, setLoadingExtractAi] = useState(false);

  const [showModal, setShowModal] = useState(false);
  const [message, setMessage] = useState('');

  const showSavePopup = () => {
    setShowModal(true);
    setTimeout(() => setShowModal(false), 1500);
  };

  const saveToServer = async (url, body) => {
    try {
      await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });
      showSavePopup();
    } catch {
      setMessage('Error saving');
    }
  };

  const genNotes = async () => {
    setLoadingSum(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-note', { method: 'POST' });
      const { note } = await res.json();
      setRawNotes(note || '');
    } catch {
      setMessage('Error generating');
    } finally {
      setLoadingSum(false);
    }
  };

  const doSumm = async () => {
    if (!rawNotes.trim() && !file) return setMessage('Paste text or upload file');
    setLoadingSum(true);
    try {
      let data;
      if (rawNotes.trim()) {
        data = await (await fetch('http://127.0.0.1:5001/summarize-text', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: rawNotes })
        })).json();
      } else {
        const form = new FormData();
        form.append('file', file);
        data = await (await fetch('http://127.0.0.1:5001/summarize', {
          method: 'POST',
          body: form
        })).json();
      }
      setSummary(data.summary || '');
    } catch {
      setMessage('Error summarising');
    } finally {
      setLoadingSum(false);
    }
  };

  const saveSum = () => {
    if (!summary) return;
    saveToServer('http://127.0.0.1:5001/save-summary', { username, content: summary });
  };

  const genExample = async () => {
    setLoadingPara(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-note', { method: 'POST' });
      const { note } = await res.json();
      setText(note || '');
    } catch {
      setMessage('Error generating');
    } finally {
      setLoadingPara(false);
    }
  };

  const doPara = async () => {
    if (!text.trim()) return setMessage('Enter or generate text');
    setLoadingPara(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/paraphrase-text', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text, tone })
      });
      const { content } = await res.json();
      setParaphrase(content || '');
    } catch {
      setMessage('Error paraphrasing');
    } finally {
      setLoadingPara(false);
    }
  };

  const savePara = () => {
    if (!paraphrase) return;
    saveToServer('http://127.0.0.1:5001/save-paraphrase', { username, content: paraphrase, tone });
  };

  return (
    <div className="study-page" style={bgStyle}>
      <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" className="streak-home" onClick={() => navigate('/home2')} />
      <h1 className="main-header">Let's Study</h1>
      {message && <div className="message">{message}</div>}

      <div className="top-row">
        <section className="section">
          <h2>Summarise Note <button className="btn" onClick={saveSum}>Save</button></h2>
          <textarea rows="4" value={rawNotes} onChange={e => setRawNotes(e.target.value)} placeholder="Paste notes..." />
          <input type="file" ref={fileInputRef} accept=".txt,.pdf" onChange={e => setFile(e.target.files[0])} />
          <button onClick={genNotes} disabled={loadingSum}>{loadingSum ? 'Generating...' : 'Generate'}</button>
          <button onClick={doSumm} disabled={loadingSum}>{loadingSum ? 'Summarising...' : 'Summarise'}</button>
          {summary && <div className="generated-output">{summary}</div>}
        </section>

        <section className="section">
          <h2>Paraphrase Note <button className="btn" onClick={savePara}>Save</button></h2>
          <textarea rows="4" value={text} onChange={e => setText(e.target.value)} placeholder="Type here..." />
          <select value={tone} onChange={e => setTone(e.target.value)}>
            <option value="casual">Casual</option>
            <option value="academic">Academic</option>
          </select>
          <button onClick={genExample} disabled={loadingPara}>{loadingPara ? 'Generating...' : 'Generate Example'}</button>
          <button onClick={doPara} disabled={loadingPara}>{loadingPara ? 'Paraphrasing...' : 'Paraphrase'}</button>
          {paraphrase && <div className="generated-output">{paraphrase}</div>}
        </section>
      </div>

      {showModal && (
        <div className="modal show">
          <div className="modal-content">✅ Saved Successfully!</div>
        </div>
      )}
    </div>
  );
}
