import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './MediaPage.css';

function MediaPage() {
  const username = localStorage.getItem('username');
  const [posts, setPosts] = useState([]);
  const [text, setText] = useState('');
  const [message, setMessage] = useState('');

  const bgUrl = `${process.env.PUBLIC_URL}/photos/pages2.jpg`;

  const fetchPosts = async () => {
    try {
      const res = await fetch('http://127.0.0.1:5001/media-posts');
      const data = await res.json();
      setPosts(data);
    } catch (err) {
      console.error('Failed to load posts:', err);
    }
  };

  const handlePost = async () => {
    if (!text.trim() || !username) {
      setMessage('‚ùå Please enter text and log in');
      return;
    }

    try {
      await fetch('http://127.0.0.1:5001/media-posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, text })
      });
      setText('');
      fetchPosts(); // refresh
      setMessage('‚úÖ Posted!');
    } catch {
      setMessage('‚ùå Failed to post');
    }

    setTimeout(() => setMessage(''), 1500);
  };

  const handleSave = async (postId) => {
    try {
      await fetch('http://127.0.0.1:5001/save-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, postId })
      });
      setMessage('‚úÖ Saved!');
    } catch {
      setMessage('‚ùå Failed to save');
    }

    setTimeout(() => setMessage(''), 1500);
  };

  const handleDelete = async (postId) => {
  try {
    await fetch(`http://127.0.0.1:5001/media-posts/${postId}`, {
      method: 'DELETE'
    });
    fetchPosts(); // ‚úÖ reload from backend
    setMessage('üóëÔ∏è Post deleted');
  } catch {
    setMessage('‚ùå Failed to delete post');
  }

  setTimeout(() => setMessage(''), 1500);
};

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div
      className="media-page"
      style={{
        backgroundImage: `url(${bgUrl})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
      }}
    >
      <Link to="/home2" className="media-home">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>

      <h1 className="media-title">Media Wall</h1>

      <div className="new-post-box">
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="What's on your mind?"
        />
        <button onClick={handlePost}>Post</button>
        {message && (
          <div className="modal show">
            <div className="modal-content">{message}</div>
          </div>
        )}
      </div>

      <div className="post-list">
        {posts.map((p, i) => (
          <div className="post-card" key={i}>
            <div className="post-username">@{p.username}</div>
            <div className="post-content">{p.text}</div>
            <div className="post-actions">
              <button className="save-btn" onClick={() => handleSave(p._id)}>
                üíæ Save
              </button>
              {p.username === username && (
                <button className="delete-btn" onClick={() => handleDelete(p._id)}>
                  üóëÔ∏è Delete
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MediaPage;
