// src/pages/StudyPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudyPage.css';

export default function StudyPage() {
  const username = localStorage.getItem('username');
  const navigate = useNavigate();

  // Paraphrase Note state
  const [text, setText] = useState('');
  const [paraphrase, setParaphrase] = useState('');
  const [tone, setTone] = useState('casual');
  const [loadingPara, setLoadingPara] = useState(false);

  // Vocab Search state
  const [searchWord, setSearchWord] = useState('');
  const [generatedWord, setGeneratedWord] = useState('');
  const [vocabDef, setVocabDef] = useState('');
  const [loadingGenWord, setLoadingGenWord] = useState(false);
  const [loadingSearch, setLoadingSearch] = useState(false);

  // Manual Vocab state
  const [manualWord, setManualWord] = useState('');
  const [manualMeaning, setManualMeaning] = useState('');

  // Extract Vocab (Manual)
  const [manualText, setManualText] = useState('');
  const [extractedManual, setExtractedManual] = useState([]);
  const [loadingExtractManual, setLoadingExtractManual] = useState(false);

  // Extract Vocab (AI)
  const [aiParagraph, setAiParagraph] = useState('');
  const [extractedAi, setExtractedAi] = useState([]);
  const [loadingGenAiPara, setLoadingGenAiPara] = useState(false);
  const [loadingExtractAi, setLoadingExtractAi] = useState(false);

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [modalMsg, setModalMsg] = useState('');

  const triggerModal = (msg) => {
    setModalMsg(msg);
    setShowModal(true);
    setTimeout(() => setShowModal(false), 1500);
  };

  // ─── Paraphrase Note Handlers ─────────────────────────────────────────

  const genExample = async () => {
    setLoadingPara(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-note', { method: 'POST' });
      const { note } = await res.json();
      setText(note);
    } catch (err) {
      console.error(err);
      triggerModal('Gen failed');
    } finally {
      setLoadingPara(false);
    }
  };

  const doPara = async () => {
    if (!text) return triggerModal('Nothing to paraphrase');
    setLoadingPara(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/paraphrase-text', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text, tone })
      });
      const { content } = await res.json();
      setParaphrase(content);
    } catch (err) {
      console.error(err);
      triggerModal('Para failed');
    } finally {
      setLoadingPara(false);
    }
  };

  const savePara = async () => {
    if (!paraphrase) return triggerModal('Nothing to save');
    try {
      const res = await fetch('http://127.0.0.1:5001/save-paraphrase', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, content: paraphrase, tone })
      });
      if (!res.ok) throw new Error(await res.text());
      triggerModal('Paraphrase saved!');
      navigate('/savepage');
    } catch (err) {
      console.error(err);
      triggerModal('Save failed');
    }
  };

  // ─── Vocab Search Handlers ────────────────────────────────────────────

  const genWord = async () => {
    setLoadingGenWord(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-word', { method: 'POST' });
      const { word } = await res.json();
      setGeneratedWord(word);
      setSearchWord(word);
    } catch (err) {
      console.error(err);
      triggerModal('Gen failed');
    } finally {
      setLoadingGenWord(false);
    }
  };

  const defineWord = async () => {
    if (!searchWord) return triggerModal('Enter a word');
    setLoadingSearch(true);
    try {
      const res = await fetch(
        `https://api.dictionaryapi.dev/api/v2/entries/en/${searchWord}`
      );
      const data = await res.json();
      const def = data[0].meanings[0].definitions[0].definition;
      setVocabDef(def);
    } catch (err) {
      console.error(err);
      triggerModal('Search failed');
    } finally {
      setLoadingSearch(false);
    }
  };

  const saveVocab = async () => {
    const word = searchWord || generatedWord;
    if (!word || !vocabDef) return triggerModal('Nothing to save');
    try {
      const res = await fetch('http://127.0.0.1:5001/save-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username,
          word,
          definition: vocabDef,
          partOfSpeech: '',
          example: '',
          pronunciation: ''
        })
      });
      if (!res.ok) throw new Error(await res.text());
      triggerModal('Vocab saved!');
      navigate('/savepage');
    } catch (err) {
      console.error(err);
      triggerModal('Save failed');
    }
  };

  // ─── Manual Vocab Handler ─────────────────────────────────────────────

  const saveManual = async () => {
    if (!manualWord || !manualMeaning) return triggerModal('Enter word & meaning');
    try {
      const res = await fetch('http://127.0.0.1:5001/save-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username,
          word: manualWord,
          definition: manualMeaning,
          partOfSpeech: '',
          example: '',
          pronunciation: ''
        })
      });
      if (!res.ok) throw new Error(await res.text());
      triggerModal('Manual vocab saved!');
      navigate('/savepage');
    } catch (err) {
      console.error(err);
      triggerModal('Save failed');
    }
  };

  // ─── Extract Vocab (Manual) Handlers ────────────────────────────────

  const extractManual = async () => {
    if (!manualText) return triggerModal('Enter text');
    setLoadingExtractManual(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/extract-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: manualText })
      });
      const { words } = await res.json();
      setExtractedManual(words);
    } catch (err) {
      console.error(err);
      triggerModal('Extract failed');
    } finally {
      setLoadingExtractManual(false);
    }
  };

  const saveExtractedManual = async () => {
    if (extractedManual.length === 0) return triggerModal('No words to save');
    try {
      await Promise.all(
        extractedManual.map((w) =>
          fetch('http://127.0.0.1:5001/save-vocab', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              username,
              word: w,
              definition: '',
              partOfSpeech: '',
              example: '',
              pronunciation: ''
            })
          })
        )
      );
      triggerModal('Extracted words saved!');
      navigate('/savepage');
    } catch (err) {
      console.error(err);
      triggerModal('Save failed');
    }
  };

  // ─── Extract Vocab (AI) Handlers ────────────────────────────────────

  const generateAiPara = async () => {
    setLoadingGenAiPara(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/generate-note', { method: 'POST' });
      const { note } = await res.json();
      setAiParagraph(note);
    } catch (err) {
      console.error(err);
      triggerModal('Gen failed');
    } finally {
      setLoadingGenAiPara(false);
    }
  };

  const extractAi = async () => {
    if (!aiParagraph) return triggerModal('No paragraph');
    setLoadingExtractAi(true);
    try {
      const res = await fetch('http://127.0.0.1:5001/extract-vocab', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: aiParagraph })
      });
      const { words } = await res.json();
      setExtractedAi(words);
    } catch (err) {
      console.error(err);
      triggerModal('Extract failed');
    } finally {
      setLoadingExtractAi(false);
    }
  };

  const saveExtractedAi = saveExtractedManual;

  return (
    <div
      className="study-page"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages4.jpg)`,
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      {/* Paraphrase Note */}
      <div className="top-row">
        <section className="section">
          <h2>
            Paraphrase Note{' '}
            <button className="btn" onClick={savePara}>
              Save
            </button>
          </h2>
          <div className="action-buttons">
            <button onClick={genExample} disabled={loadingPara}>
              {loadingPara ? 'Gen…' : 'Gen'}
            </button>
            <button onClick={doPara} disabled={loadingPara}>
              {loadingPara ? 'Para…' : 'Para'}
            </button>
            <button onClick={() => setText('')}>Clear</button>
          </div>
          <textarea
            rows="4"
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Enter or generate…"
          />
          <select value={tone} onChange={(e) => setTone(e.target.value)}>
            <option>casual</option>
            <option>academic</option>
          </select>
          {paraphrase && (
            <div className="generated-output">
              <div dangerouslySetInnerHTML={{ __html: paraphrase }} />
            </div>
          )}
        </section>
      </div>

      {/* Vocab Search & Manual Vocab */}
      <div className="middle-row">
        <section className="section">
          <h2>
            Vocab Search{' '}
            <button className="btn" onClick={saveVocab}>
              Save
            </button>
          </h2>
          <div className="action-buttons">
            <button onClick={genWord} disabled={loadingGenWord}>
              {loadingGenWord ? 'Gen…' : 'Gen'}
            </button>
            <button
              onClick={() => {
                setSearchWord('');
                setVocabDef('');
              }}
            >
              Clear
            </button>
          </div>
          <input
            type="text"
            value={searchWord}
            onChange={(e) => setSearchWord(e.target.value)}
            placeholder="Enter word…"
          />
          <button onClick={defineWord} disabled={loadingSearch}>
            {loadingSearch ? 'Search…' : 'Search'}
          </button>
          {vocabDef && <div className="generated-output">{vocabDef}</div>}
        </section>

        <section className="section">
          <h2>
            Manual Vocab{' '}
            <button className="btn" onClick={saveManual}>
              Save
            </button>
          </h2>
          <input
            type="text"
            value={manualWord}
            onChange={(e) => setManualWord(e.target.value)}
            placeholder="Word…"
          />
          <textarea
            rows="3"
            value={manualMeaning}
            onChange={(e) => setManualMeaning(e.target.value)}
            placeholder="Meaning…"
          />
        </section>
      </div>

      {/* Extract Vocab */}
      <div className="extract-row">
        <section className="section">
          <h2>
            Extract Vocab (Manual){' '}
            <button className="btn" onClick={saveExtractedManual}>
              Save
            </button>
          </h2>
          <textarea
            rows="4"
            value={manualText}
            onChange={(e) => setManualText(e.target.value)}
            placeholder="Paste paragraph…"
          />
          <div className="action-buttons">
            <button onClick={extractManual} disabled={loadingExtractManual}>
              {loadingExtractManual ? 'Ext…' : 'Ext'}
            </button>
            <button
              onClick={() => {
                setManualText('');
                setExtractedManual([]);
              }}
            >
              Clear
            </button>
          </div>
          {extractedManual.length > 0 && (
            <ul className="extracted-list">
              {extractedManual.map((w) => (
                <li key={w}>{w}</li>
              ))}
            </ul>
          )}
        </section>

        <section className="section">
          <h2>
            Extract Vocab (AI){' '}
            <button className="btn" onClick={saveExtractedAi}>
              Save
            </button>
          </h2>
          <div className="action-buttons">
            <button onClick={generateAiPara} disabled={loadingGenAiPara}>
              {loadingGenAiPara ? 'Gen…' : 'Gen'}
            </button>
            <button
              onClick={() => {
                setAiParagraph('');
                setExtractedAi([]);
              }}
            >
              Clear
            </button>
          </div>
          {aiParagraph && <div className="generated-output">{aiParagraph}</div>}
          {aiParagraph && (
            <button onClick={extractAi} disabled={loadingExtractAi}>
              {loadingExtractAi ? 'Ext…' : 'Ext'}
            </button>
          )}
          {extractedAi.length > 0 && (
            <ul className="extracted-list">
              {extractedAi.map((w) => (
                <li key={w}>{w}</li>
              ))}
            </ul>
          )}
        </section>
      </div>

      {/* Glass-style Save Confirmation Modal */}
      <div className={`modal ${showModal ? 'show' : ''}`}>
        <div className="modal-content">{modalMsg}</div>
      </div>
    </div>
  );
}
