// src/pages/MediaPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './MediaPage.css';

export default function MediaPage() {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState('');
  const username = localStorage.getItem('username');

  const fetchPosts = async () => {
    const res = await fetch('http://127.0.0.1:5001/media-posts');
    const data = await res.json();
    setPosts(data);
  };

  const handlePost = async () => {
    if (!newPost.trim()) return;
    await fetch('http://127.0.0.1:5001/create-post', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, content: newPost })
    });
    setNewPost('');
    fetchPosts();
  };

  const handleSave = async (post) => {
    await fetch('http://127.0.0.1:5001/save-media', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, content: post.content })
    });
    alert('‚úÖ Saved to Media!');
  };

  useEffect(() => { fetchPosts(); }, []);

  return (
    <div className="media-page">
      <Link to="/home2" className="media-home">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>

      <h1 className="media-title">üì¢ Global Study Board</h1>

      <div className="new-post-box">
        <textarea 
          placeholder="What's on your mind?" 
          value={newPost} 
          onChange={e => setNewPost(e.target.value)} 
        />
        <button onClick={handlePost}>Post</button>
      </div>

      <div className="post-list">
        {posts.map(post => (
          <div key={post._id} className="post-card">
            <p className="post-username">@{post.username}</p>
            <p className="post-content">{post.content}</p>
            <p className="post-time">{new Date(post.createdAt).toLocaleString()}</p>
            <button className="save-btn" onClick={() => handleSave(post)}>‚≠ê Save</button>
          </div>
        ))}
      </div>
    </div>
  );
}
