// src/pages/MediaPage.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './CommonPage.css';

function MediaPage() {
  const username = localStorage.getItem('username');
  const [text, setText] = useState('');
  const [posts, setPosts] = useState([]);

  const bgUrl = `${process.env.PUBLIC_URL}/photos/user-bg.jpg`;

  const fetchPosts = async () => {
    try {
      const res = await fetch('http://127.0.0.1:5001/saved-media');
      const data = await res.json();
      setPosts(data);
    } catch (err) {
      console.error('Error loading posts', err);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const handlePost = async () => {
    if (!text.trim()) return;
    try {
      await fetch('http://127.0.0.1:5001/post-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, text })
      });
      setText('');
      fetchPosts();
    } catch (err) {
      console.error('Error posting', err);
    }
  };

  return (
    <div className="common-page" style={{
      backgroundImage: `url(${bgUrl})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center'
    }}>
      <Link to="/home2" className="home-link">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>
      <h1>Media</h1>

      <div className="media-post-box">
        <textarea
          placeholder="Write your post..."
          value={text}
          onChange={e => setText(e.target.value)}
        />
        <button onClick={handlePost}>Post</button>
      </div>

      <div className="media-feed">
        {posts.map(post => (
          <div key={post._id} className="media-post">
            <p>{post.text}</p>
            <small>{new Date(post.createdAt).toLocaleString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MediaPage;
