import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './CommonPage.css';

function MediaPage() {
  const username = localStorage.getItem('username');
  const [posts, setPosts] = useState([]);
  const [text, setText] = useState('');
  const [message, setMessage] = useState('');

  const bgUrl = `${process.env.PUBLIC_URL}/photos/pages2.jpg`;

  const fetchPosts = async () => {
    try {
      const res = await fetch('http://127.0.0.1:5001/media-posts');
      const data = await res.json();
      setPosts(data.reverse()); // newest first
    } catch (err) {
      console.error('Failed to load posts:', err);
    }
  };

  const handlePost = async () => {
    if (!text.trim() || !username) {
      setMessage('âŒ Please enter text and log in');
      return;
    }

    try {
      await fetch('http://127.0.0.1:5001/media-posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, text })
      });
      setText('');
      fetchPosts();
      setMessage('âœ… Posted!');
    } catch {
      setMessage('âŒ Failed to post');
    }

    setTimeout(() => setMessage(''), 1500);
  };

  const handleSave = async (text) => {
    try {
      await fetch('http://127.0.0.1:5001/save-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, text })
      });
      setMessage('âœ… Saved!');
    } catch {
      setMessage('âŒ Failed to save');
    }

    setTimeout(() => setMessage(''), 1500);
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div className="common-page" style={{
      backgroundImage: `url(${bgUrl})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      minHeight: '100vh',
      color: '#fff'
    }}>
      <Link to="/home2" className="home-link">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>

      <h1 style={{ marginTop: '2rem' }}>Media Wall</h1>

      <div style={{ width: '80%', margin: '1rem auto' }}>
        <textarea
          rows="3"
          value={text}
          placeholder="What's on your mind?"
          onChange={e => setText(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '10px',
            marginBottom: '10px',
            resize: 'none'
          }}
        />
        <button onClick={handlePost} style={{
          padding: '10px 20px',
          borderRadius: '8px',
          background: '#2196f3',
          color: '#fff',
          border: 'none',
          cursor: 'pointer'
        }}>Post</button>
        {message && <div className="modal show"><div className="modal-content">{message}</div></div>}
      </div>

      <div style={{ width: '80%', margin: '0 auto' }}>
        {posts.map((p, i) => (
          <div key={i} style={{
            background: 'rgba(255,255,255,0.15)',
            backdropFilter: 'blur(8px)',
            borderRadius: '10px',
            padding: '10px 20px',
            marginBottom: '1rem',
            color: '#fff'
          }}>
            <strong>@{p.username}</strong>
            <p style={{ whiteSpace: 'pre-wrap' }}>{p.text}</p>
            <div style={{ textAlign: 'right' }}>
              <button onClick={() => handleSave(p.text)} style={{
                background: '#4caf50',
                color: '#fff',
                border: 'none',
                padding: '5px 10px',
                borderRadius: '5px',
                cursor: 'pointer'
              }}>
                ðŸ’¾ Save
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MediaPage;
