// src/pages/MediaPage.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './CommonPage.css';

function MediaPage() {
  const username = localStorage.getItem('username');
  const bgUrl = `${process.env.PUBLIC_URL}/photos/user-bg.jpg`;
  const [postText, setPostText] = useState('');
  const [posts, setPosts] = useState([]);
  const [message, setMessage] = useState('');

  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => setMessage(''), 1500);
  };

  const fetchPosts = async () => {
    try {
      const res = await fetch('http://127.0.0.1:5001/media-posts');
      const data = await res.json();
      setPosts(data);
    } catch (err) {
      showMessage('âŒ Failed to load posts');
    }
  };

  const handlePost = async () => {
    if (!postText.trim()) return showMessage('âŒ Cannot post empty text');
    try {
      await fetch('http://127.0.0.1:5001/post-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, text: postText })
      });
      setPostText('');
      fetchPosts();
      showMessage('âœ… Posted');
    } catch {
      showMessage('âŒ Failed to post');
    }
  };

  const handleSave = async (postId) => {
    try {
      await fetch('http://127.0.0.1:5001/save-media', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, postId })
      });
      showMessage('âœ… Saved post');
    } catch {
      showMessage('âŒ Failed to save');
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div className="common-page" style={{ backgroundImage: `url(${bgUrl})` }}>
      <Link to="/home2" className="home-link">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>
      <h1 className="main-header">ðŸ“¢ Media</h1>

      {message && <div className="popup-msg">{message}</div>}

      <div className="media-container">
        <textarea
          value={postText}
          onChange={e => setPostText(e.target.value)}
          placeholder="What's on your mind?"
        />
        <button onClick={handlePost}>Post</button>

        <div className="media-posts">
          {posts.map(p => (
            <div className="media-post" key={p._id}>
              <p className="post-text">{p.text}</p>
              <p className="post-meta">By {p.username} on {new Date(p.createdAt).toLocaleString()}</p>
              <button onClick={() => handleSave(p._id)}>Save</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MediaPage;
