import React, { useState, useEffect } from 'react';
import './CommonPage.css';
import { Link } from 'react-router-dom';

function MediaPage() {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState('');
  const username = localStorage.getItem('username');

  useEffect(() => {
    fetch('http://127.0.0.1:5001/media-posts')
      .then(res => res.json())
      .then(data => setPosts(data))
      .catch(err => console.error('❌ Load error:', err));
  }, []);

  const handlePost = async () => {
    if (!newPost.trim()) return;
    const res = await fetch('http://127.0.0.1:5001/media-posts', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, text: newPost })
    });
    const saved = await res.json();
    setPosts([saved, ...posts]);
    setNewPost('');
  };

  const handleSave = async (post) => {
    await fetch('http://127.0.0.1:5001/save-media', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, text: post.text })
    });
    alert('✅ Post saved!');
  };

  const bgStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/photos/user-bg.jpg)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
    padding: '2rem',
    color: 'white',
    fontFamily: 'sans-serif'
  };

  return (
    <div style={bgStyle}>
      <Link to="/home2" className="home-link">
        <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" />
      </Link>
      <h1 style={{ textAlign: 'center' }}>Media</h1>

      <div style={{ maxWidth: 500, margin: '0 auto', marginBottom: 20 }}>
        <textarea
          rows="3"
          value={newPost}
          onChange={(e) => setNewPost(e.target.value)}
          placeholder="What's on your mind?"
          style={{
            width: '100%',
            padding: '1rem',
            borderRadius: '10px',
            border: 'none',
            resize: 'none',
            backgroundColor: 'rgba(255,255,255,0.2)',
            color: 'white'
          }}
        />
        <button onClick={handlePost} style={{
          marginTop: '10px',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '8px',
          backgroundColor: '#4caf50',
          color: 'white',
          cursor: 'pointer',
          fontWeight: 'bold'
        }}>Post</button>
      </div>

      {posts.map((p, i) => (
        <div key={i} style={{
          backgroundColor: 'rgba(255, 255, 255, 0.15)',
          padding: '1rem',
          borderRadius: '10px',
          marginBottom: '1rem',
          maxWidth: 500,
          marginLeft: 'auto',
          marginRight: 'auto'
        }}>
          <p style={{ marginBottom: '0.5rem' }}><strong>@{p.username}</strong></p>
          <p style={{ whiteSpace: 'pre-wrap' }}>{p.text}</p>
          <button onClick={() => handleSave(p)} style={{
            marginTop: '0.5rem',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            padding: '5px 10px',
            cursor: 'pointer'
          }}>Save</button>
        </div>
      ))}
    </div>
  );
}

export default MediaPage;
