// src/pages/StudyPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudyPage.css';

export default function StudyPage() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const fileInputRef = useRef(null);

  // Background
  const bgStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/photos/pages4.jpg)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  };

  // STATE
  const [rawNotes, setRawNotes] = useState('');
  const [file, setFile] = useState(null);
  const [summary, setSummary] = useState('');
  const [loadingSum, setLoadingSum] = useState(false);
  const [savedSummaries, setSavedSummaries] = useState([]);

  const [text, setText] = useState('');
  const [tone, setTone] = useState('casual');
  const [paraphrase, setParaphrase] = useState('');
  const [loadingPara, setLoadingPara] = useState(false);
  const [savedParaphrases, setSavedParaphrases] = useState([]);

  const [searchWord, setSearchWord] = useState('');
  const [vocabDef, setVocabDef] = useState('');
  const [loadingGenWord, setLoadingGenWord] = useState(false);
  const [loadingSearch, setLoadingSearch] = useState(false);
  const [savedVocabList, setSavedVocabList] = useState([]);

  const [manualWord, setManualWord] = useState('');
  const [manualMeaning, setManualMeaning] = useState('');

  const [manualText, setManualText] = useState('');
  const [extractedManual, setExtractedManual] = useState([]);
  const [loadingExtractManual, setLoadingExtractManual] = useState(false);

  const [aiParagraph, setAiParagraph] = useState('');
  const [extractedAi, setExtractedAi] = useState([]);
  const [loadingGenAiPara, setLoadingGenAiPara] = useState(false);
  const [loadingExtractAi, setLoadingExtractAi] = useState(false);
  const [showModal, setShowModal] = useState(false);


  const [message, setMessage] = useState('');

  // Load user data
  const loadData = async () => {
    try {
      const [sumRes, paraRes, vocabRes] = await Promise.all([
        fetch(`http://127.0.0.1:5001/summaries?username=${username}`),
        fetch(`http://127.0.0.1:5001/paraphrases?username=${username}`),
        fetch(`http://127.0.0.1:5001/vocab-list?username=${username}`),
      ]);
      setSavedSummaries(await sumRes.json());
      setSavedParaphrases(await paraRes.json());
      setSavedVocabList(await vocabRes.json());
    } catch (e) {
      console.error(e);
    }
  };

  useEffect(() => { if (username) loadData(); }, [username]);

  // Handlers
  const genNotes = async () => { setLoadingSum(true); setMessage(''); try { const res=await fetch('http://127.0.0.1:5001/generate-note',{method:'POST'}); const {note}=await res.json(); setRawNotes(note||''); } catch { setMessage('Error generating'); } finally { setLoadingSum(false);} };
  const doSumm = async () => { if(!rawNotes.trim()&&!file){setMessage('Paste or file');return} setLoadingSum(true);setMessage('');try{let data;if(rawNotes.trim()){data=await(await fetch('http://127.0.0.1:5001/summarize-text',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text:rawNotes})})).json()}else{const f=new FormData();f.append('file',file);data=await(await fetch('http://127.0.0.1:5001/summarize',{method:'POST',body:f})).json()}setSummary(data.summary||'')}catch{setMessage('Error summarising')}finally{setLoadingSum(false)}};
  const saveSum = async () => { if(!summary)return; try{await fetch('http://127.0.0.1:5001/save-summary',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,content:summary})});showSavePopup();loadData();navigate('/save')}catch{setMessage('Error saving')} };

  const genExample = async () => { setLoadingPara(true); setMessage(''); try { const res=await fetch('http://127.0.0.1:5001/generate-note',{method:'POST'}); const {note}=await res.json(); setText(note||''); } catch { setMessage('Error gen'); } finally { setLoadingPara(false);} };
  const doPara = async () => { if(!text.trim()){setMessage('Type/generate');return} setLoadingPara(true);setMessage('');try{const res=await fetch('http://127.0.0.1:5001/paraphrase-text',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text,tone})});const{content}=await res.json();setParaphrase(content||'')}catch{setMessage('Error para')}finally{setLoadingPara(false)}};
  const savePara = async () => { if(!paraphrase)return; try{await fetch('http://127.0.0.1:5001/save-paraphrase',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,content:paraphrase,tone})});showSavePopup();loadData();navigate('/save')}catch{setMessage('Error saving')}};

  const genWord = async () => { setLoadingGenWord(true); setMessage(''); try{const res=await fetch('http://127.0.0.1:5001/generate-word',{method:'POST'});const{word}=await res.json();setSearchWord(word||'')}catch{setMessage('Err gen')}finally{setLoadingGenWord(false)}};
  const defineWord = async () => { if(!searchWord.trim()){setMessage('Type word');return} setLoadingSearch(true);setMessage('');try{const res=await fetch('http://127.0.0.1:5001/define-word',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({word:searchWord.trim()})});const{content}=await res.json();setVocabDef(content||'')}catch{setMessage('Err define')}finally{setLoadingSearch(false)}};
  const saveVocab = async () => { if(!searchWord||!vocabDef)return; try{await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:searchWord,definition:vocabDef})});showSavePopup();loadData();navigate('/save')}catch{setMessage('Err save')}};

  const saveManual = async () => { if(!manualWord.trim()||!manualMeaning.trim()){setMessage('Both');return} try{await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:manualWord,definition:manualMeaning})});setMessage('Saved');loadData();navigate('/save')}catch{setMessage('Err')}};

  const extractManual = async () => { if(!manualText.trim()){setMessage('Para');return} setLoadingExtractManual(true);setMessage('');try{const res=await fetch('http://127.0.0.1:5001/extract-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text:manualText})});const{words}=await res.json();setExtractedManual(words||[])}catch{setMessage('Err extract')}finally{setLoadingExtractManual(false)}};
  const saveExtractedManual = async () => { if(!extractedManual.length)return; try{for(const w of extractedManual)await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:w,definition:''})});showSavePopup();loadData();navigate('/save')}catch{setMessage('Err save')}};

  const generateAiPara = async () => { setLoadingGenAiPara(true); setMessage(''); try{const res=await fetch('http://127.0.0.1:5001/generate-note',{method:'POST'});const{note}=await res.json();setAiParagraph(note||'')}catch{setMessage('Err gen')}finally{setLoadingGenAiPara(false)}};
  const extractAi = async () => { if(!aiParagraph.trim()){setMessage('Gen first');return} setLoadingExtractAi(true);setMessage('');try{const res=await fetch('http://127.0.0.1:5001/extract-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({text:aiParagraph})});const{words}=await res.json();setExtractedAi(words||[])}catch{setMessage('Err extract')}finally{setLoadingExtractAi(false)}};
  const saveExtractedAi = async () => { if(!extractedAi.length)return; try{for(const w of extractedAi)await fetch('http://127.0.0.1:5001/save-vocab',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({username,word:w,definition:''})});showSavePopup();loadData();navigate('/save')}catch{setMessage('Err save')}};
  const showSavePopup = () => {
    setShowModal(true);
    setTimeout(() => setShowModal(false), 1500); // Hide after 1.5s
  };

  return (
    <div className="study-page" style={bgStyle}>
      <img src={`${process.env.PUBLIC_URL}/photos/home.jpg`} alt="Home" className="streak-home" onClick={()=>navigate('/home2')} />
      <h1 className="main-header">Let’s Study</h1>
      {message && <div className="message">{message}</div>}

      <div className="top-row">
        <section className="section">
          <h2>Summarise Note <button className="btn" onClick={saveSum}>Save</button></h2>
          <textarea rows="4" value={rawNotes} onChange={e=>setRawNotes(e.target.value)} placeholder="Paste notes…" />
          <div className="action-buttons">
            <button onClick={()=>setRawNotes('')}>Clear Text</button>
            <input ref={fileInputRef} type="file" accept=".txt,.pdf" onChange={e=>setFile(e.target.files[0])} />
            <button onClick={()=>{setFile(null);fileInputRef.current.value='';}}>Clear File</button>
            <button onClick={genNotes} disabled={loadingSum}>{loadingSum?'Generate':'Gen'}</button>
          </div>
          <button onClick={doSumm} disabled={loadingSum}>{loadingSum?'Summarise':'Summ'}</button>
          {summary && <div className="generated-output">{<div dangerouslySetInnerHTML={{__html:summary}}/>}</div>}
        </section>

        <section className="section">
          <h2>Paraphrase Note <button className="btn" onClick={savePara}>Save</button></h2>
          <div className="action-buttons">
            <button onClick={genExample} disabled={loadingPara}>{loadingPara?'Generate':'Gen'}</button>
            <button onClick={doPara} disabled={loadingPara}>{loadingPara?'Paraphrase':'Para'}</button>
            <button onClick={()=>setText('')}>Clear</button>
          </div>
          <textarea rows="4" value={text} onChange={e=>setText(e.target.value)} placeholder="Enter or generate…" />
          <select value={tone} onChange={e=>setTone(e.target.value)}><option>casual</option><option>academic</option></select>
          {paraphrase && <div className="generated-output">{<div dangerouslySetInnerHTML={{__html:paraphrase}}/>}</div>}
        </section>
      </div>

      <div className="middle-row">
        <section className="section">
          <h2>Vocab Search <button className="btn" onClick={saveVocab}>Save</button></h2>
          <div className="action-buttons">
            <button onClick={genWord} disabled={loadingGenWord}>{loadingGenWord?'Generate':'Gen'}</button>
            <button onClick={()=>{setSearchWord('');setVocabDef('');}}>Clear</button>
          </div>
          <input type="text" value={searchWord} onChange={e=>setSearchWord(e.target.value)} placeholder="Enter word…" />
          <button onClick={defineWord} disabled={loadingSearch}>{loadingSearch?'Search':'Search'}</button>
          {vocabDef && <div className="generated-output">{vocabDef}</div>}
        </section>

        <section className="section">
          <h2>Manual Vocab <button className="btn" onClick={saveManual}>Save</button></h2>
          <input type="text" value={manualWord} onChange={e=>setManualWord(e.target.value)} placeholder="Word…" />
          <textarea rows="3" value={manualMeaning} onChange={e=>setManualMeaning(e.target.value)} placeholder="Meaning…" />
        </section>
      </div>

      <div className="extract-row">
        <section className="section">
          <h2>Extract Vocab (Manual) <button className="btn" onClick={saveExtractedManual}>Save</button></h2>
          <textarea rows="4" value={manualText} onChange={e=>setManualText(e.target.value)} placeholder="Paste paragraph…" />
          <div className="action-buttons">
            <button onClick={extractManual} disabled={loadingExtractManual}>{loadingExtractManual?'Exit':'Ext'}</button>
            <button onClick={()=>{setManualText('');setExtractedManual([]);}}>Clear</button>
          </div>
          {extractedManual.length>0 && <ul className="extracted-list">{extractedManual.map(w=><li key={w}>{w}</li>)}</ul>}
        </section>

        <section className="section">
          <h2>Extract Vocab (AI) <button className="btn" onClick={saveExtractedAi}>Save</button></h2>
          <div className="action-buttons">
            <button onClick={generateAiPara} disabled={loadingGenAiPara}>{loadingGenAiPara?'Generate':'Gen'}</button>
            <button onClick={()=>{setAiParagraph('');setExtractedAi([]);}}>Clear</button>
          </div>
          {aiParagraph && <div className="generated-output">{aiParagraph}</div>}
          {aiParagraph && <button onClick={extractAi} disabled={loadingExtractAi}>{loadingExtractAi?'Exit':'Ext'}</button>}
          {extractedAi.length>0 && <ul className="extracted-list">{extractedAi.map(w=><li key={w}>{w}</li>)}</ul>}
        </section>
      </div>
        {showModal && (
          <div className="modal show">
            <div className="modal-content">✅ Saved Successfully!</div>
          </div>
        )}
    </div>

  );

}
